const servicesList=document.querySelectorAll('.services-item');const servicesDescList=document.querySelectorAll('.services-item-more');NodeList.prototype.indexOf=Array.prototype.indexOf;for(const service of servicesList){service.onclick=function(evt){evt.preventDefault();let index=servicesList.indexOf(service);if(!service.classList.contains('active')){servicesList.forEach(element=>{if(servicesList.indexOf(element)!==index){element.classList.remove('active')}});service.classList.add('active')};if(!servicesDescList[index].classList.contains('active')){servicesDescList.forEach(element=>{if(servicesDescList.indexOf(element)!==index){element.classList.remove('active')}});servicesDescList[index].classList.add('active')}}}
